package Assignments.Assignment_7;

public class Question_5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=7;
		for(int j=1;j<=num;j++) {

			for(int count1=0; count1<(num-j);count1++) {
				System.out.print("1");

			}
			for(int count2=0; count2<j;count2++) {
				System.out.print(j);

			}
			System.out.println();
		}

	}

}
