public class Task17_avarageCalculation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double n1, n2, n3, avarage;
		n1=10; 
		n2=35; 
		n3=50;
		avarage=(n1+n2+n3)/3;
		
		System.out.println("Avarage of 3 numbers " + n1 +", " + n2 + " and " + n3 + " is " + avarage);
	}

}
