package PART_I_Core.day10_controlFlowStatements_part4;

public class Task46 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=7;
		
		System.out.println("Multiplication Table for " +num);
		System.out.println("---------------------------");
		
		for (int i=1;i<=10;i++) {
			System.out.println(num + "*" +i + " = " + (num*i));
		}
	}

}
